import React, { useState, useEffect } from 'react';

function App() {
  // State to hold the API response
  const [data, setData] = useState(null);

  // Fetch data from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:5000/api/message')  // This is your Node.js backend API
      .then(response => response.json())
      .then(data => setData(data.message))      // Store the message in state
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ border: '1px solid #ccc', padding: '20px', borderRadius: '8px', width: '300px', textAlign: 'center' }}>
        <h1>API Response</h1>
        <p>{data ? data : 'Loading...'}</p>
      </div>
    </div>
  );
}

export default App;
