import React, { useState, useEffect } from 'react';

function StoreList() {
  const [stores, setStores] = useState([]);
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Fetch stores and handle errors
    fetch('http://localhost:5000/stores')
      .then((response) => response.json())
      .then((data) => setStores(data?.data || []))  // Ensure that data exists
      .catch((error) => console.error('Error fetching data:', error));

    // Fetch books and handle errors
    fetch('http://localhost:5000/books')
      .then((response) => response.json())
      .then((data) => setBooks(data?.data || []))  // Ensure that data exists
      .catch((error) => console.error('Error fetching books:', error));
  }, []);

  // Helper function to format the date as dd.mm.yyyy
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are 0-based
    const year = date.getFullYear();
    return `${day}.${month}.${year}`;
  };

  // Helper function to get books for each store
  const getStoreBooks = (storeBooks) => {
    // Check if storeBooks is defined and is an array
    if (!storeBooks || !Array.isArray(storeBooks)) {
      return [];
    }
    // Map through the store's book relationships and find matching books in the books state
    return storeBooks.map(bookRel => books.find(book => book.id === bookRel.id) || { attributes: { name: 'Unknown Book' }, relationships: { author: { data: { fullName: 'Unknown Author' } } } });
  };

  // Helper function to get the author of a book
  const getBookAuthor = (book) => {
    if (!book || !book.relationships || !book.relationships.author) return 'Unknown';
    const author = book.relationships.author.data?.fullName || 'Unknown Author';  // Fallback to 'Unknown Author' if fullName is missing
    return author;
  };

  return (
    <div style={containerWrapperStyle}>
      <h1 style={headerStyle}>Store List</h1>
      <div style={storeListStyle}>
        {stores.map((store) => (
          <div key={store.id} style={storeCardStyle}>
            <div style={storeDetailsStyle}>
              <img 
                src={store.attributes?.storeImage || 'https://via.placeholder.com/150'} 
                alt={store.attributes?.name || 'Unknown Store'} 
                style={imageStyle} 
              />

              {/* Store Info (name, rating, website) */}
              <div style={infoContainerStyle}>
                <h2>{store.attributes?.name || 'Unknown Store'}</h2>
                <p><strong>Rating:</strong> {store.attributes?.rating || 'N/A'}</p>
                <p><strong>Website:</strong> <a href={store.attributes?.website || '#'} target="_blank" rel="noreferrer">{store.attributes?.website || 'N/A'}</a></p>
              </div>
            </div>

            {/* Display formatted establishment date */}
            <p><strong>Established:</strong> {formatDate(store.attributes?.establishmentDate || new Date())}</p>

            {/* Table displaying Best-Selling Books */}
            <table style={tableStyle}>
              <thead>
                <tr>
                  <th colSpan="2" style={headerStyle}>Best-Selling Books</th>
                </tr>
                <tr>
                  <th>Book Name</th>
                  <th>Author</th>
                </tr>
              </thead>
              <tbody>
                {getStoreBooks(store.relationships?.books?.data || []).map((book, index) => (
                  <tr key={index}>
                    <td>{book.attributes?.name || 'Unknown Book'}</td>
                    <td>{getBookAuthor(book)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
}

// Styles
const containerWrapperStyle = {
  textAlign: 'center',
  padding: '20px',
};

const headerStyle = {
  fontSize: '2rem',
  marginBottom: '20px',
};

const storeListStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '20px',
  alignItems: 'center',
};

const storeCardStyle = {
  border: '1px solid #ccc',
  borderRadius: '8px',
  padding: '20px',
  width: '500px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  textAlign: 'left',
};

const storeDetailsStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '20px',
};

const imageStyle = {
  width: '150px',
  height: '150px',
  objectFit: 'cover',
  borderRadius: '50%',
  border: '2px solid #ccc',
};

const infoContainerStyle = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
};

const tableStyle = {
  width: '100%',
  marginTop: '20px',
  borderCollapse: 'collapse',
};

export default StoreList;
