import './App.css';
import React, { useState, useEffect } from 'react';

function StoreList() {
  const [stores, setStores] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/stores')
      .then(response => response.json())
      .then(data => setStores(data.data))  // Access the stores data
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div style={containerWrapperStyle}>
      <h1 style={headerStyle}>Store List</h1>
      <div style={storeListStyle}>
        {stores.map((store) => (
          <div key={store.id} style={storeCardStyle}>
            <img 
              src={store.attributes.storeImage} 
              alt={store.attributes.name} 
              style={imageStyle} 
            />
            <h2>{store.attributes.name}</h2>
            <p><strong>Website:</strong> <a href={store.attributes.website} target="_blank" rel="noreferrer">{store.attributes.website}</a></p>
            <p><strong>Rating:</strong> {store.attributes.rating}</p>
            <p><strong>Established:</strong> {new Date(store.attributes.establishmentDate).toLocaleDateString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

// Basic styles for layout
const containerWrapperStyle = {
  textAlign: 'center',
  padding: '20px',
};

const headerStyle = {
  fontSize: '2rem',
  marginBottom: '20px',
};

const storeListStyle = {
  display: 'flex',
  flexDirection: 'column',  // Stack items vertically
  gap: '20px',              // Space between store cards
  alignItems: 'center',      // Center align the cards horizontally
};

const storeCardStyle = {
  border: '1px solid #ccc',
  borderRadius: '10px',
  padding: '20px',
  width: '1500px',           // Set a fixed width for the store cards
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  textAlign: 'left',
};

const imageStyle = {
  width: '150px',          // Fixed width
  height: '150px',         // Fixed height
  objectFit: 'cover',    // Scale the image down without cropping
  borderRadius: '50%',
  marginBottom: '10px',
  backgroundColor: '#f0f0f0' // Optional background color to fill empty space
};

export default StoreList;
